context.global.where_builder = function(where){ 
	var whereQuery = "";

	for(var i = 0; i < where.length; i++){
		whereQuery += where[i].col + " ";
		whereQuery += where[i].op + " ";
		whereQuery += where[i].val + " ";	
		whereQuery += where[i].chain_op + " ";
	}

	return whereQuery;
}

