var table_name = msg.payload.table_name;
var where = msg.payload.where;

var query = "select * from " + table_name + " " 
	where ; 

msg.topic = query 

// select one
if(msg.req.path.indexOf("_one"))
{
	return [null, msg]; 
} else {
// select all
	return [msg,null];
}

var params = msg.req.query; 

var where_params = {};
var where_query = "";

if(typeof(params.where) != "undefined"){
	where_params = JSON.parse(params.where) 

	for(var i = 0; i < where_params.length; i++){
		where_query += where_params[i].col + " ";
		where_query += where_params[i].op + " ";
		where_query += where_params[i].val + " ";	
		if(typeof(where_params[i].chain_op) != "undefined")
			where_query += where_params[i].chain_op + " ";
	} 
}

msg.payload = {
	table_name: params.table_name,
	where: where_query 
}

return msg;

var params = msg.req.body; 

var table_params = {};
var table_param_query = "";

if(typeof(params.table_params) != "undefined")
{ 
	table_params = JSON.parse(params.table_params) 

	for(var i = 0; i < table_params.length; i++){
		table_param_query += table_params[i].col + " ";
		table_param_query += table_params[i].type + " ";
		table_param_query += table_params[i].option + " ";	
		if(i < table_params.length - 1)
			table_param_query += ", ";
	} 
}

msg.payload.	table_name = params.table_name;
msg.payload.table_parameter = "(" + table_param_query + ")";

return msg;

var params = msg.req.body; 

var col_query = "";
var val_query = "";

if(typeof(params.cols) != "undefined")
{ 
	var columns = JSON.parse(params.cols) 

	for(var i = 0; i < columns.length; i++){
		col_query += columns[i] + " ";	
		if(i < columns.length - 1)
			col_query += ", ";
	} 
}

if(typeof(params.vals) != "undefined")
{ 
	var values = JSON.parse(params.vals) 

	for(var i = 0; i < values.length; i++){
		val_query += "'" + values[i] + "'";	
		if(i < values.length - 1)
			val_query += ", ";
	} 
}

msg.payload.	table_name = params.table_name;
msg.payload.col_query = "(" + col_query + ")";
msg.payload.val_query = "(" + val_query + ")";

return msg;
